import React, { useContext, useEffect } from 'react';
import useMobileDetect from 'use-mobile-detect-hook';
import { Container } from 'react-bootstrap';
import { PhotoCard } from './PhotoCard';
import { WebSocketContext } from '../context/WebSocket';

export function PhotoList() {
  const { photoList } = useContext(WebSocketContext);
  const detectMobile = useMobileDetect();
  useEffect(() => {
    console.log('PhotoList');
    console.log(photoList);
  }, [photoList]);

  return (
    <div className="">
      <Container>
        {detectMobile.isMobile() ?
          (<div>sssssssssss</div>)
          :
          (<div>
            <ul className="topImagesGrid">
              {
                Array.isArray(photoList) && photoList.length > 2 &&
                photoList.slice(0, 3).map((element, index) => (
                  <li key={index}>
                    <PhotoCard albumId={element.albumId} id={element.id} title={element.title} url={element.url} thumbnailUrl={element.thumbnailUrl} type="first" />
                  </li>
                ))
              }
            </ul>
            <ul className="imagesGrid">
              {
                Array.isArray(photoList) && photoList.length > 3 &&
                photoList.slice(3).map((element, index) => (
                  <li key={index}>
                    <PhotoCard albumId={element.albumId} id={element.id} title={element.title} url={element.url} thumbnailUrl={element.thumbnailUrl} type="notfirst" />
                  </li>
                ))
              }
            </ul>
          </div>)
        }
      </Container>
    </div>
  );
}
