import React, { useState } from 'react';
import { Button, Card } from 'react-bootstrap';

import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faIndustry } from '@fortawesome/free-solid-svg-icons';
import { PhotoUpdateModal } from './PhotoUpdateModal';

export function PhotoCard({ albumId, id, title ,url,thumbnailUrl}) {
  const [showModal, setShowModal] = useState(false);

  return (
    <>
      <Card style={{ width: '18rem' }}>
        <Card.Body>
          <Card.Title>{title}</Card.Title>
          <Card.Text as="div">
            <div className="d-flex flex-row justify-content-center align-items-center">
              <FontAwesomeIcon color={state ? 'green' : 'red'} icon={faIndustry} size="5x" />
            </div>
            <br />
          </Card.Text>
          <img src={url} alt="BigCo Inc. logo"/>
        </Card.Body>
        <Card.Footer className="text-muted">
          <Button variant="primary" onClick={() => { setShowModal(true); }}>Update</Button>
        </Card.Footer>
      </Card>
      <PhotoUpdateModal
        show={showModal}
        onClose={() => { setShowModal(false); }}
        title={`${name} - Update`}
        data={{ status: state, message, id, name }}
      />
    </>
  );
}

FactoryCard.propTypes = {
  id: PropTypes.number.isRequired,
  name: PropTypes.string.isRequired,
  state: PropTypes.bool,
  message: PropTypes.string,
};

FactoryCard.defaultProps = {
  message: 'İşler tıkırında!',
  state: false,
};
