import React, { useState } from 'react';
import { Button, Card } from 'react-bootstrap';

import PropTypes from 'prop-types';
import { PhotoUpdateModal } from './PhotoUpdateModal';

export function PhotoCard({ albumId, id, title, url, thumbnailUrl }) {
  const [showModal, setShowModal] = useState(false);
  const [showTitle, setShowTitle] = useState(false);

  const onClickImage = () => {
    setShowTitle(!showTitle);

  };
  return (
    <div onClick={onClickImage}>
      {!showTitle && <img className="cardImg" src={url} alt="BigCo Inc. logo" />}
      {showTitle && <Card.Title>{title}</Card.Title>}
      <PhotoUpdateModal
        show={showModal}
        onClose={() => { setShowModal(false); }}
        title={`${title} - Update`}
        data={{ albumId, title, id, thumbnailUrl, url }}
      />
      <Button variant="primary" onClick={() => { setShowModal(true); }}>Update</Button>



    </div>
  );
}

PhotoCard.propTypes = {
  albumId: PropTypes.number.isRequired,
  id: PropTypes.number.isRequired,
  title: PropTypes.string.isRequired,
  thumbnailUrl: PropTypes.string.isRequired,
  url: PropTypes.string.isRequired,
};
