import React, { useContext, useEffect } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { PhotoCard } from './PhotoCard';
import { WebSocketContext } from '../context/WebSocket';

export function PhotoList() {
  const { photoList } = useContext(WebSocketContext);

  useEffect(() => {
    console.log('PhotoList');
    console.log(photoList);
  }, [photoList]);

  return (
    <div className="h-100 d-flex flex-row justify-content-center align-items-center">
      <Container>
        {
            Array.isArray(photoList) &&
            photoList.reduce((accumulator, currentValue, currentIndex, array) => {
              if (currentIndex % 3 === 0) {
                accumulator.push(array.slice(currentIndex, currentIndex + 3));
              }
              return accumulator;
            }, []).map((element, index) => (
              <Row key={index} className="my-5">
                <Col><PhotoCard albumId={element[0].albumId} id={element[0].id} title={element[0].title} url={element[0].url} thumbnailUrl={element[0].thumbnailUrl} /></Col>
                <Col><PhotoCard albumId={element[1].albumId} id={element[1].id} title={element[1].title} url={element[1].url} thumbnailUrl={element[1].thumbnailUrl} /></Col>
                <Col><PhotoCard albumId={element[2].albumId} id={element[2].id} title={element[2].title} url={element[2].url} thumbnailUrl={element[2].thumbnailUrl} /></Col>
              </Row>
            ))
        }
        <ul className="imagesGrid">
          {
            Array.isArray(photoList) && photoList.length > 0 &&
            photoList.map((element, index) => (
              <li key={index}><PhotoCard albumId={element[0].albumId} id={element[0].id} title={element[0].title} url={element[0].url} thumbnailUrl={element[0].thumbnailUrl} /></li>
            ))
          }
        </ul>

      </Container>
    </div>
  );
}
